def create_signature(query_string, secret_key):
    return hmac.new(secret_key.encode(), query_string.encode(), hashlib.sha256).hexdigest()

def get_contract_addresses():
    timestamp = str(int(time.time() * 1000))
    query_string = f"timestamp={timestamp}"
    signature = create_signature(query_string, SECRET_KEY)
    headers = {"X-MEXC-APIKEY": API_KEY}
    url = f"{MEXC_API_URL}/capital/config/getall?{query_string}&signature={signature}"

    response = requests.get(url, headers=headers)

    valid_networks = {
        "Solana(SOL)": "solana",
        "BNB Smart Chain(BEP20)": "bsc",
        "Ethereum(ERC20)": "ethereum",
        "Polygon(MATIC)": "polygon",
        "Avalanche C-Chain": "avalanche",
        "Arbitrum One": "arbitrum",
        "Optimism": "optimism",
        "Fantom": "fantom",
    }

    if response.status_code == 200:
        data = response.json()
        contract_list = []
        for token in data:
            coin = token.get("coin")
            for network in token.get("networkList", []):
                contract = network.get("contract")
                network_name = network.get("network")
                if network_name in valid_networks:
                    withdraw_fee = network.get("withdrawFee", 0)
                    withdraw_min = network.get("withdrawMin", "N/A")
                    withdraw_max = network.get("withdrawMax", "N/A")
                    deposit_enable = network.get("depositEnable", False)
                    withdraw_enable = network.get("withdrawEnable", False)

                    contract_list.append({
                        "symbol": coin,
                        "contract_address": contract,
                        "network": valid_networks[network_name],
                        "withdraw_fee": withdraw_fee,
                        "withdraw_min": withdraw_min,
                        "withdraw_max": withdraw_max,
                        "deposit_enable": deposit_enable,
                        "withdraw_enable": withdraw_enable
                    })
        return contract_list
    else:
        print(f"‚ùå Error getting contracts: {response.status_code} - {response.text}")
        return []

def get_dexscreener_price(chain_id, contract_address):
    try:
        url = f"{DEX_API_URL}/token-pairs/v1/{chain_id}/{contract_address}"
        response = requests.get(url, timeout=30)
        response.raise_for_status()

        data = response.json()
        if isinstance(data, list):
            pairs = data
        elif isinstance(data, dict) and 'pairs' in data:
            pairs = data['pairs']
        else:
            print(f"‚ö†Ô∏è Unexpected DexScreener response format for {chain_id}/{contract_address}")
            return None, None

        best_pair = None
        highest_liquidity = 0

        for pair in pairs:
            liquidity = pair.get('liquidity', {}).get('usd', 0)
            if liquidity > highest_liquidity:
                highest_liquidity = liquidity
                best_pair = pair

        if best_pair:
            price_usd = best_pair.get('priceUsd')
            if price_usd:
                print(f"‚ÑπÔ∏è DEX Price for {contract_address}: ${float(price_usd):.8f} (Liquidity: ${highest_liquidity:.2f})")
                return float(price_usd), highest_liquidity
        print(f"‚ö†Ô∏è No valid price found for {contract_address} on {chain_id}")
        return None, None
    except requests.exceptions.RequestException as e:
        print(f"‚ùå DexScreener request error for {chain_id}/{contract_address}: {str(e)}")
        return None, None

def get_order_book(symbol):
    symbol += "USDT"
    url = f"{MEXC_API_URL}/depth"
    params = {'symbol': symbol, 'limit': 40}
    try:
        response = requests.get(url, params=params)
        if response.status_code == 200:
            return response.json()
        else:
            print(f"‚ùå MEXC order book error for {symbol}: {response.status_code} - {response.text}")
            return None
    except requests.exceptions.RequestException as e:
        print(f"‚ùå MEXC order book request error for {symbol}: {str(e)}")
        return None

def get_mexc_price(symbol):
    url = f"{MEXC_API_URL}/ticker/price?symbol={symbol}USDT"
    try:
        response = requests.get(url).json()
        if 'price' in response:
            price = float(response['price'])
            print(f"‚ÑπÔ∏è MEXC Price for {symbol}: ${price:.8f}")
            return price
        print(f"‚ö†Ô∏è MEXC price not found for {symbol}")
        return None
    except requests.exceptions.RequestException as e:
        print(f"‚ùå MEXC price request error for {symbol}: {str(e)}")
        return None

def get_mexc_sell_price(symbol, target_amount=TRADE_VOLUME):
    order_book = get_order_book(symbol)
    if not order_book:
        print(f"‚ö†Ô∏è No order book data for {symbol} (sell price)")
        return None

    total_price = 0
    total_quantity = 0
    total_value = 0

    for ask in order_book['asks']:
        price = float(ask[0])
        quantity = float(ask[1])
        value = price * quantity

        if total_value < target_amount:
            total_price += price * quantity
            total_quantity += quantity
            total_value += value

        if total_value >= target_amount:
            break

    if total_quantity == 0:
        print(f"‚ö†Ô∏è No asks in order book for {symbol}")
        return None

    average_price = total_price / total_quantity
    print(f"‚ÑπÔ∏è MEXC Sell Price for {symbol} (${target_amount} volume): ${average_price:.8f}")
    return round(average_price, 8)

def get_mexc_buy_price(symbol, target_amount=TRADE_VOLUME):
    order_book = get_order_book(symbol)
    if not order_book:
        print(f"‚ö†Ô∏è No order book data for {symbol} (buy price)")
        return None

    total_price = 0
    total_quantity = 0
    total_value = 0

    for bid in order_book['bids']:
        price = float(bid[0])
        quantity = float(bid[1])
        value = price * quantity

        if total_value < target_amount:
            total_price += price * quantity
            total_quantity += quantity
            total_value += value

        if total_value >= target_amount:
            break

    if total_quantity == 0:
        print(f"‚ö†Ô∏è No bids in order book for {symbol}")
        return None

    average_price = total_price / total_quantity
    print(f"‚ÑπÔ∏è MEXC Buy Price for {symbol} (${target_amount} volume): ${average_price:.8f}")
    return round(average_price, 8)

